import*as utilMethods from"../utils.js";import*as questionLogic from"../sharedQuestionLogic.js";const flashCard=document.getElementById("flash-card"),flashAnswer=document.getElementById("flash-answer"),flashNumOne=document.getElementById("flash-first-number"),flashNumTwo=document.getElementById("flash-second-number"),flashOpOne=document.getElementById("flash-first-operator");function flashHandler(e){var t=utilMethods.calculation(parseInt(flashNumOne.innerHTML),parseInt(flashNumTwo.innerHTML),flashOpOne.innerHTML);if(flashAnswer.textContent=String(t),this.classList.contains("flip")){t=sessionStorage.getItem("activeOperators");if(!t)throw new Error("Session Storage Item not retrieved");questionLogic.newQuestion("flash",t)}this.classList.toggle("flip"),e.preventDefault()}flashCard.addEventListener("mousedown",flashHandler,!1),window.onload=function(){var e=sessionStorage.getItem("activeOperators"),t=sessionStorage.getItem("activeDifficulty");if(!e||!t)throw new Error("Session Storage Item not retrieved");utilMethods.updateGeneralSelected(e,t),questionLogic.newQuestion("flash",e)};