const modalImage=document.querySelector(".badge-modal-image"),modalContainer=document.querySelector(".badge-modal-container");function playPulseAnimation(){modalImage&&modalContainer&&(modalContainer.style.display="flex",modalContainer.style.zIndex="1000",modalImage.classList.add("pulse"))}function stopPulseAnimation(){modalImage&&modalContainer&&(modalImage.classList.remove("pulse"),modalContainer.style.display="none",modalContainer.style.zIndex="0")}async function animateBadge(){playPulseAnimation(),setTimeout(()=>{stopPulseAnimation(),modalImage&&(modalImage.style.opacity="0")},2e3)}export{animateBadge};