const modalImage=document.querySelector(".badge-modal-image"),modalContainer=document.querySelector(".badge-modal-container");function playPulseAnimation(){modalContainer.style.display="flex",modalContainer.style.zIndex="1000",modalImage.classList.add("pulse")}function stopPulseAnimation(){modalImage.classList.remove("pulse"),modalContainer.style.display="none",modalContainer.style.zIndex="0"}async function animateBadge(){playPulseAnimation(),setTimeout(()=>{stopPulseAnimation(),modalImage.style.opacity=0},2e3)}export{animateBadge};