const burgerContainer=document.getElementById("burger-container"),expandedMenu=document.getElementById("expanded-menu"),burgerSpans=document.querySelectorAll(".burger span"),burger=document.getElementById("burger");function toggleMenu(){burger.classList.toggle("active"),expandedMenu.classList.toggle("active"),burgerSpans.forEach(e=>{e.classList.toggle("active")})}document.addEventListener("click",function(e){burgerContainer.contains(e.target)||expandedMenu.contains(e.target)||(burger.classList.remove("active"),expandedMenu.classList.remove("active"),burgerSpans.forEach(e=>{e.classList.remove("active")}))}),burgerContainer.addEventListener("click",toggleMenu),burger.addEventListener("click",function(e){e.stopPropagation(),toggleMenu()}),burgerSpans.forEach(e=>{e.addEventListener("click",function(e){e.stopPropagation(),toggleMenu()})}),document.querySelectorAll("#expanded-menu li a, #close-menu").forEach(e=>{e.addEventListener("click",toggleMenu)});const testNav=document.getElementById("test-nav"),practiceNav=document.getElementById("practice-nav");import{state}from"./state.js";testNav.addEventListener("click",function(){state.type="real",window.location.href="/tests",utilMethods.loadSection("real-menu")}),practiceNav.addEventListener("click",function(){state.type="practice",window.location.href="/practice",utilMethods.loadSection("practice-menu")});