const burgerContainer=document.getElementById("burger-container"),expandedMenu=document.getElementById("expanded-menu"),burgerSpans=document.querySelectorAll(".burger span"),burger=document.getElementById("burger");function toggleMenu(){burger.classList.toggle("active"),expandedMenu.classList.toggle("active"),burgerSpans.forEach(e=>{e.classList.toggle("active")})}document.addEventListener("click",function(e){burgerContainer.contains(e.target)||expandedMenu.contains(e.target)||(burger.classList.remove("active"),expandedMenu.classList.remove("active"),burgerSpans.forEach(e=>{e.classList.remove("active")}))}),burgerContainer.addEventListener("click",toggleMenu),burger.addEventListener("click",function(e){e.stopPropagation(),toggleMenu()}),burgerSpans.forEach(e=>{e.addEventListener("click",function(e){e.stopPropagation(),toggleMenu()})}),document.querySelectorAll("#expanded-menu li a, #close-menu").forEach(e=>{e.addEventListener("click",toggleMenu)});const testNav=document.getElementById("test-nav"),practiceNav=document.getElementById("practice-nav"),learningNav=document.getElementById("learning-nav");testNav.addEventListener("click",function(){window.location.href="/tests"}),practiceNav.addEventListener("click",function(){window.location.href="/practice"}),learningNav.addEventListener("click",function(){window.location.href="/learning"});