const carousel=document.querySelector(".fraction-carousel"),lessons=document.querySelectorAll(".fraction-lesson");let currentPosition=0;function showLesson(e){carousel.style.transform=`translateX(-${100*e}%)`}const previousBtn=document.querySelector(".fraction-previous-btn"),nextBtn=document.querySelector(".fraction-next-btn");previousBtn.addEventListener("click",()=>{0<currentPosition&&showLesson(--currentPosition)}),nextBtn.addEventListener("click",()=>{currentPosition<lessons.length-1&&showLesson(++currentPosition)});